import {  Inject } from '@angular/core';
import { CandidatService } from '../Services/CandidatService';
import { QuestionService } from '../Services/QuestionService';
import { QuestionnaireService } from '../Services/Questionnaire';
import { QuizService } from '../Services/QuizService';

import { Component, OnInit } from '@angular/core';
import { ScaleType, Color } from '@swimlane/ngx-charts';
@Component({
  selector: 'app-statistiques',
  templateUrl: './statistiques.component.html',
  styleUrls: ['./statistiques.component.css']
})

export class StatistiquesComponent implements OnInit {
  barChartData: any[];
  pieChartData!: any[];
  lineChartData!: any[];
  areaChartData!: any[];
  totalCandidatsCount!: number;
  totalQuestionsCount!: number;
  totalQuestionnairesCount!: number;
  totalQuizzesCount!: number;

  view: [number, number] = [350, 250]; // Taille des graphiques

  // options
  showXAxis: boolean = true;
  showYAxis: boolean = true;
  gradient: boolean = false;
  showLegend: boolean = true;
  showXAxisLabel: boolean = true;
  xAxisLabel: string = 'Niveau';
  showYAxisLabel: boolean = true;
  yAxisLabel: string = 'Nombre de candidats';

  colorScheme: Color = {
    name: 'custom',
    selectable: true,
    group: ScaleType.Ordinal,
    domain: ['#0000FF', '#FFFF00', '#808080'] // Bleu, Jaune, Gris
  };

  constructor(private candidatService: CandidatService,
    private questionService: QuestionService,
    private questionnaireService: QuestionnaireService,
    private quizService: QuizService,
  ) {this.barChartData = [];
  }

  ngOnInit(): void {
    this.loadChartData();
  }

  loadChartData() {
    this.candidatService.getTotalCandidatsCount().subscribe((count: number) => {
      this.totalCandidatsCount = count;
      this.updateBarChartData();
    });

    this.questionService.getTotalQuestionCount().subscribe((count: number) => {
      this.totalQuestionsCount = count;
      this.updateBarChartData();
    });

    this.questionnaireService.getTotalQuestionnaireCount().subscribe((count: number) => {
      this.totalQuestionnairesCount = count;
      this.updateBarChartData();
    });

    this.quizService.getTotalQuizCount().subscribe((count: number) => {
      this.totalQuizzesCount = count;
      this.updateBarChartData();
    });
  }

  updateBarChartData() {
    this.barChartData = [
      {
        name: 'Candidats',
        series: [
          { name: 'Total', value: this.totalCandidatsCount }
        ]
      },
      {
        name: 'Questions',
        series: [
          { name: 'Total', value: this.totalQuestionsCount }
        ]
      },
      {
        name: 'Questionnaires',
        series: [
          { name: 'Total', value: this.totalQuestionnairesCount }
        ]
      },
      {
        name: 'Quizzes',
        series: [
          { name: 'Total', value: this.totalQuizzesCount }
        ]
      }
    ];
  }
